
console.log($(global.window).outerWidth());
var Breakpoints = require("../jquery.breakpoints.js");

describe("Breakpoints Tests", function() {
  Object.defineProperty(window, 'innerWidth', {
    value: 1200
  });

  describe("Initialized", function() {
    it("Default window width", function() {
      var winW = $(window).outerWidth();
      expect(winW).toEqual(1200);
    });

    it("Resize event", function() {

      //var spyEvent = spyOnEvent('window', 'resize');

       window.resizeTo(1201, 1);
       var winW = $(window).outerWidth();
       expect(winW).toEqual(1201);
      //  expect('resize').toHaveBeenTriggeredOn('window');
     });

  });

  describe("Breakpoint Change Trigger", function() {
    console.log('@width', $(window).outerWidth());
    //console.log('@window', $(window).width());
    var a;

    it("and so is a spec", function() {
      a = true;

      expect(a).toBe(true);
    });
  });
});
